rules = """
    1. Lipsum encoded string starts with {intro} referred to as the 'intro.'
    2. All non-alpha sequences are a single separator.
    3. The following four letter words are used (without regard to case) to represent bytes (byte value is the index in the following list): {byte_words}
    4. All words not in this list are considered filler and ignored.
    5. '{end}' denotes the end of a lipsum encoded string (subsequent characters are ignored).
""".format(intro=intro, byte_words=byte_words, end=end)


def to_lipsum(input):
    if len(input) == 0:
        raise ValueError("Invalid Lipsum: Input is empty")

    content = [byte_words[ord(b) if type(input[0]) == str else b] for b in input]

    import random

    sentences = []

    while len(content) > 0:
        sentence = []
        for i in range(random.randint(2, 4)):
            if len(content) > 1:
                sentence.extend([random.choice(filler), content.pop(0), content.pop(0)])
            if random.choice([True, False]) and len(content) > 0:
                sentence.append(content.pop(0))

        if len(sentence) > 10:
            if random.random():
                r = random.randint(3, 8)
                sentence[r] = sentence[r] + ','

        sentences.append(' '.join(sentence).capitalize() + random.choice("....................?!"))

    sentences[-1] = sentences[-1][0:-1] + ' ex.'

    return intro + '. ' + ' '.join(sentences)


def from_lipsum(lipsum):
    import re

    if not lipsum.startswith(intro):
        raise ValueError("Invalid Lipsum: No '{}' found".format(intro))

    if not end in lipsum:
        raise ValueError("Invalid Lipsum: No '{}' found".format(end))

    b = bytearray()
    for w in re.split("[^a-z]+", lipsum.split(intro, 1)[1].split(end)[0].lower()):
        if len(w) == 4:
            b.append(byte_words.index(w))
    return b
